package controller

import akka.util.ByteString
import org.scalatest.{BeforeAndAfterEach, FunSuite, Matchers}
import org.scalatestplus.play.PlaySpec
import org.scalatestplus.play.guice.GuiceOneAppPerSuite
import play.api.test.FakeRequest

import scala.concurrent.{Await, Future}
import scala.concurrent.duration._
import play.api.test.Helpers._
import repo.{FractalRepo, FractalRow}


class FractalControllerSpec extends FunSuite with Matchers with GuiceOneAppPerSuite with BeforeAndAfterEach{
 val data = "data:image/png;base64,"

  val request = FakeRequest().withRawBody(ByteString(data.stripPrefix("data:image/png;base64,")))
    .withHeaders("Content-Type" -> "image/png")

  val repo = app.injector.instanceOf[FractalRepo]
  val controller = app.injector.instanceOf[FractalController]

  test("upload images") {
    val id = "123"
    repo.save(FractalRow(id, None))
    val response = Await.result(controller.putImage(id).apply(request), 1.second)
    contentAsString(Future.successful(response)) shouldBe ""
    response.header.status shouldBe 200
  }
}
